mobileAction.processQrCodeFunction:
$scope = widgetContext.$scope;
let $injector = $scope.$injector;
let $translate = $injector.get(widgetContext.servicesMap.get('translate'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let deviceNotFound = $translate.instant('widgets.input-widgets.claim-not-found');
let failedClaimDevice = $translate.instant('widgets.input-widgets.claim-failed');
let claimDeviceInfo = JSON.parse(code);
let deviceName = claimDeviceInfo.deviceName;
let secretKey = claimDeviceInfo.secretKey;
let claimRequest = {
    secretKey: secretKey
};
deviceService.claimDevice(deviceName, claimRequest, { ignoreErrors: true }).subscribe(
    function (data) {
        widgetContext.showSuccessToast('Device \'' + deviceName + '\' successfully claimed!');
        widgetContext.updateAliases();
    },
    function (error) {
        if(error.status == 404) {
            widgetContext.showErrorToast(deviceNotFound);
        } else {
            if (error.status !== 400 && error.error && error.error.message) {
                showDialog('Failed to claim device', error.error.message);
            } else {
                widgetContext.showErrorToast(failedClaimDevice);
            }
        }
    }
);

function showDialog(title, error) {
    setTimeout(function() {
        widgetContext.dialogs.alert(title, error).subscribe();
    }, 100);
}

