mobileAction.processLocationFunction:
// Function body to process current location of the phone. 
// - latitude - phone location latitude
// - longitude - phone location longitude

saveEntityLocationAttributes('latitude', 'longitude', latitude, longitude);


function saveEntityLocationAttributes(latitudeAttributeName, longitudeAttributeName, latitude, longitude) {
    const entityId = widgetContext.stateController.getStateParams().entityId;
    if (entityId) {
        let attributes = [
            { key: latitudeAttributeName, value: latitude },
            { key: longitudeAttributeName, value: longitude }
        ];
        widgetContext.attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributes).subscribe(
           function() {
               widgetContext.showSuccessToast('Updated location!');
           },
           function(error) {
               widgetContext.showErrorToast('Updated location failed');
           }
        );
    }
}

